/**
	CJ 1/21/2016 This function will add an ID to the manufacturedOrganization section of Immunizations

	@param {String} arg1 - the CDA to be modified
	@return {String} return returns a modified CDA
*/
function addManufacturerOrganizationIDInImmunizationSection(arg1) {
//CJ 1/21/2016 This code will add an ID to the manufacturedOrganization section of Immunizations
	var compLen = msg['component']['structuredBody']['component'].length();
//iterate through each component section
	for (var i=0;i<compLen;i++) {
//find the section with a code of 11369-6 which means the section is for Immunization
		var compCode = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
		if (compCode === '11369-6') {
//iterate through each entry of the Immunizations section
		var entryLen = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
			for (var j=0;j<entryLen;j++) {
//iterate through each consumable manufacturerOrganization of the entry

				var manufacturerOrganizationLen = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct']['manufacturerOrganization'].length();
				for (var k=0;k<manufacturerOrganizationLen;k++) {
//if the id for the manufacturerOrganization is blank then create from the standardIndustryClassCode Code
					var manufacturerOrganizationID = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct']['manufacturerOrganization'][k]['id']['@extension'].toString();
					if (manufacturerOrganizationID === '') {
						var standardIndustryClassCode = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct']['manufacturerOrganization'][k]['standardIndustryClassCode']['@code'].toString();
						if (standardIndustryClassCode === '') {
						msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct']['manufacturerOrganization'][k]['id']['@extension'] = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct']['manufacturerOrganization'][k]['name'].toString();
						}
					else {
						msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct']['manufacturerOrganization'][k]['id']['@root'] = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct']['manufacturerOrganization'][k]['standardIndustryClassCode']['@codeSystem'].toString();
						msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct']['manufacturerOrganization'][k]['id']['@extension'] = standardIndustryClassCode.toString();
					}
					}
				break;
				}
			break;
			}
		}
	}
}

/**
	Chetan 12/2/2016 

	Putting the general code for clean up the CCD or Delete blank or empty nodes before transforming to TRN HL7 message

	@param {String} arg1 - the CDA to be modified
	@return {String} return returns a modified CDA
*/
function CDACleanupforHL7TRN(CDA) {
    try {
        var pvLname = '' + msg['documentationOf']['serviceEvent']['performer']['assignedEntity']['assignedPerson']['name'].toString();
    } catch (err) {
        var pvLname = '';
    }
    if (pvLname.length == 0) {
        delete msg['documentationOf']['serviceEvent']['performer']['assignedEntity'];
    }

    try {
        var compLen = msg['component']['structuredBody']['component'].length();
    } catch (err) {
        var compLen = 0;
    }
    for (var i = 0; i < compLen; i++) {

        try {
            var compTitle = '' + msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
        } catch (err) {
            var compTitle = '';
        }
        if (compTitle.length == 0) {
            try {
                var compTitle = '' + msg['component']['structuredBody']['component'][i]['section']['title'].toString();
            } catch (err) {
                var compTitle = '';
            }
        }

        if ((compTitle == '30954-2') || compTitle.toUpperCase() == 'LAB RESULTS') {
            try {
                var compText = '' + msg['component']['structuredBody']['component'][i]['section']['text']['content'].toString();
                compText = compText.toUpperCase();
            } catch (err) {
                var compText = '';
            }
            if (compText.search('NO LAB RESULT') != -1) {
                delete msg['component']['structuredBody']['component'][i];
                break;
            }
        }
    }

    try {
        var compLen = msg['component']['structuredBody']['component'].length();
    } catch (err) {
        var compLen = 0;
    }
    for (var i = 0; i < compLen; i++) {

        try {
            var compTitle = '' + msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
        } catch (err) {
            var compTitle = '';
        }
        if (compTitle.length == 0) {
            try {
                var compTitle = '' + msg['component']['structuredBody']['component'][i]['section']['title'].toString();
            } catch (err) {
                var compTitle = '';
            }
        }

        if ((compTitle == '48768-6') || compTitle.toUpperCase() == 'INSURANCE') {
            delete msg['component']['structuredBody']['component'][i];
            break;
        }
    }

	return msg;
}

/**
	CJ 1/21/2016 This function will change the Immunization Status to Pending if the Negation Indicator is True

	@param {String} arg1 - the CDA to be modified
	@return {String} return returns a modified CDA
*/
function changeStatusOfImmunizationIfNotGiven(arg1) {
//CJ 1/21/2016 This code will remove Unknown Allergies from Machine Readable portion of CCD
	var compLen = msg['component']['structuredBody']['component'].length();
//iterate through each component section
	for (var i=0;i<compLen;i++) {
//find the section with a code of 11369-6 which means the section is for Immunizations
		var compCode = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
		if (compCode === '11369-6') {
//iterate through each entry of the Immunizations section
			var entryLen = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
			for (var j=0;j<entryLen;j++) {
//find the Negation Indicator for the subastanceAdministration element
					var negationInd = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['@negationInd'].toString();
					if (negationInd == 'true') {
						msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['statusCode']['@code'] = 'pending';
					}
				}
			}
		}
	}

/**
	Chetan 12/2/2016 

	Creating TRN message from Cleaned CDA message.

	@param {String} CDA - CDA to be converted
	@param {String} Fac - Facilty/datasource
	@return {String} returns the converted HL7 message.
*/
function ConvertCDAtoHL7TRN(msg,Fac) 
{

//Fetch Random number
function getRandomInt(min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
}

var uuid = UUIDGenerator.getUUID();
channelMap.put('MessageKey', uuid);

var msgStr = connectorMessage.getRawData().toString();
msgStr = HL7_Encode_CDA(msgStr);

//Convert the CCD message into Base64 
var encoded = FileUtil.encode(new java.lang.String(msgStr).getBytes()).replaceAll(' \\s','');

var encodedStr = ''+encoded.toString(); // Above adds line breaks
encodedStr = encodedStr.replace(/\r?\n|\r/g, "");

//Make combination on Facility ID and DOB so that uniqueness of CCD would be maintain for each specific facility
//var dob = ''+msg.*::['recordTarget'].*::['patientRole'].*::['patient'].*::['birthTime']['@value'].toString();
//var UniqueID = Fac + '_' + dob.substr(0,8);
// Rather than creating a unique ID, use the documentID of the CCD so that the HL7 can be linked to the CCD
//var uniqueID = msg.*::['id']['@extension'].toString();
var uniqueID = msg.*::['id']['@extension'].toString();

//Create HL7 messages
var newHl7msg = <HL7Message/>;

//Create MSH segment
//createSegment('MSH', newHl7msg);
newHl7msg.MSH['MSH.1'] = '|';
newHl7msg.MSH['MSH.2'] = '^~\\&';
newHl7msg.MSH['MSH.3']['MSH.3.1'] = 'CCDA';
newHl7msg.MSH['MSH.4']['MSH.4.1'] = Fac;
newHl7msg.MSH['MSH.6']['MSH.6.1'] = 'UHIN';
newHl7msg.MSH['MSH.7']['MSH.7.1'] = DateUtil.getCurrentDate("yyyyMMddHHmmss");
newHl7msg.MSH['MSH.9'] = 'ORU^R01^2'
newHl7msg.MSH['MSH.10']['MSH.10.1'] = uniqueID;
newHl7msg.MSH['MSH.11']['MSH.11.1'] = 'P';
newHl7msg.MSH['MSH.12']['MSH.12.1'] = '2.3.1';

//Create PID segment
createSegmentAfter('PID', newHl7msg['MSH']);
newHl7msg.PID['PID.1']['PID.1.1'] = '1';
newHl7msg.PID['PID.3']['PID.3.1'] = msg.*::['recordTarget'].*::['patientRole'].*::['id'][0]['@extension'].toString();
newHl7msg.PID['PID.3']['PID.3.4'] = Fac;
newHl7msg.PID['PID.5']['PID.5.1'] = msg.*::['recordTarget'].*::['patientRole'].*::['patient'].*::['name'].*::['family'].toString();
newHl7msg.PID['PID.5']['PID.5.2'] = msg.*::['recordTarget'].*::['patientRole'].*::['patient'].*::['name'].*::['given'][0].toString();
try {
	newHl7msg.PID['PID.5']['PID.5.3'] = msg.*::['recordTarget'].*::['patientRole'].*::['patient'].*::['name'].*::['given'][1].toString();
} catch(err) {

}

var dob = ''+msg.*::['recordTarget'].*::['patientRole'].*::['patient'].*::['birthTime']['@value'].toString();
if (dob.length > 8) {
	newHl7msg.PID['PID.7']['PID.7.1'] = dob.substr(0,8);
} else if (dob.length == 8) {
	newHl7msg.PID['PID.7']['PID.7.1'] = dob;
}
var gender = msg.*::['recordTarget'].*::['patientRole'].*::['patient'].*::['administrativeGenderCode']['@code'].toString();

if (gender == 'M') {
	newHl7msg.PID['PID.8']['PID.8.1'] = 'Male';
} else if (gender == 'F') {
	newHl7msg.PID['PID.8']['PID.8.1'] = 'Female';
} else {
	newHl7msg.PID['PID.8']['PID.8.1'] = 'Other';
}

newHl7msg.PID['PID.11']['PID.11.1'] = msg.*::['recordTarget'].*::['patientRole'].*::['addr'].*::['streetAddressLine'].toString();
newHl7msg.PID['PID.11']['PID.11.3'] = msg.*::['recordTarget'].*::['patientRole'].*::['addr'].*::['city'].toString();
newHl7msg.PID['PID.11']['PID.11.4'] = msg.*::['recordTarget'].*::['patientRole'].*::['addr'].*::['state'].toString();
newHl7msg.PID['PID.11']['PID.11.5'] = msg.*::['recordTarget'].*::['patientRole'].*::['addr'].*::['postalCode'].toString();
newHl7msg.PID['PID.11']['PID.11.6'] = msg.*::['recordTarget'].*::['patientRole'].*::['addr'].*::['country'].toString();

//Creating PV1 segment
createSegmentAfter('PV1', newHl7msg['PID']);
newHl7msg.PV1['PV1.1']['PV1.1.1'] = '1';
newHl7msg.PV1['PV1.2']['PV1.2.1'] = 'O';
newHl7msg.PV1['PV1.19']['PV1.19.1'] = 'CCD_'+ uniqueID;


//Create TXA segment
createSegmentAfter('TXA', newHl7msg['PV1']);
var dos = ''+msg.*::['effectiveTime']['@value'].toString();
if (dos.length >= 12) {
	dos = dos.substr(0,12);
	newHl7msg.TXA['TXA.4']['TXA.4.1'] = dos;
	newHl7msg.TXA['TXA.6']['TXA.6.1'] = dos;
	newHl7msg.TXA['TXA.7']['TXA.7.1'] = dos;
	newHl7msg.TXA['TXA.8']['TXA.8.1'] = dos;
}
try {
	var pvID = ''+ msg.*::['documentationOf'].*::['serviceEvent'].*::['performer'].*::['assignedEntity'].*::['id']['@root'].toString();
	var pvLname = ''+ msg.*::['documentationOf'].*::['serviceEvent'].*::['performer'].*::['assignedEntity'].*::['assignedPerson']['name'].toString();
} catch(err) {
	var pvID = '';
	var pvLname = '';
}
newHl7msg.TXA['TXA.9'] = '';
newHl7msg.TXA['TXA.11'] = '';
if (pvLname.length > 0) {
	newHl7msg.TXA['TXA.9']['TXA.9.1'] = pvID;
	newHl7msg.TXA['TXA.9']['TXA.9.2'] = pvLname;
	newHl7msg.TXA['TXA.11']['TXA.11.1'] = pvID;
	newHl7msg.TXA['TXA.11']['TXA.11.2'] = pvLname;	
}
newHl7msg.TXA['TXA.12']['TXA.12.1'] = 'CCD_'+uniqueID;

//Create OBX segment
createSegmentAfter('OBX', newHl7msg['TXA']);
newHl7msg.OBX['OBX.2']['OBX.2.1'] = 'ED' ;
newHl7msg.OBX['OBX.3']['OBX.3.1'] = uniqueID;
newHl7msg.OBX['OBX.3']['OBX.3.2'] = 'Transcription'
newHl7msg.OBX['OBX.5']['OBX.5.1'] = encodedStr + '^text^html^';


//Seialize the HL7 message
var newHl72Xmsg = SerializerFactory.getSerializer('HL7V2').fromXML(newHl7msg);

channelMap.put('newHl72Xmsg',newHl72Xmsg);

}

/**
	Decodes a base64 encoded string and returns a text string

	@param {String} base64EncodedString - the string base64 encoded
	@return {String} Returns a variable named "decodedStringText" that is the decoded base 64 string
*/
function decodeBase64EncodedString(base64EncodedString) {
	decodedStringByteArray = FileUtil.decode(base64EncodedString);
	var decodedStringText = new java.lang.String(decodedStringByteArray);
	channelMap.put('decodedStringText',decodedStringText);
}

/**
	Encodes a string to a base64 encoded string and returns a variable for the base64 encoded string

	@param {String} stringToEncode - the string to base64 encoded
	@return {String} Returns a variable named "base64EncodedString" that is the encoded base 64 string
*/
function encodeStringToBase64(stringToEncode) {
	var base64EncodedString = FileUtil.encode(new java.lang.String(stringToEncode).getBytes()).replaceAll('\\s','');
	channelMap.put('base64EncodedString',base64EncodedString);
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/

// MUST BE THE LAST FUNCTION CALL AFTER ALL *_NG FUNCTIONS

function finalXfrm_NG(arg1) {
var myMsg = ''+msg.toString();
//Required final step for missing ID sections above, replacing all nullFlavors in CCDA
myMsg = myMsg.replace(/<id nullFlavor=/g, '<id root=');
myMsg = myMsg.replace(/<code nullFlavor=/g, '<code code=');
myMsg = myMsg.replace(/nullFlavor="AXXN8/g, 'code="');
// MR displays blank entries as long as there is a root or code, so change the UNK ones back to nullFlavor
myMsg = myMsg.replace(/<id root="UNK"/g, '<id nullFlavor="UNK"');
myMsg = myMsg.replace(/<code code="UNK"/g, '<code nullFlavor="UNK"');
myMsg = myMsg.replace(/<id root="NA"/g, '<id nullFlavor="NA"');
myMsg = myMsg.replace(/<code code="NI"/g, '<code nullFlavor="NI"');

//For BVCHC, all lab panels began with text below. Removing to gain space as this text was uneccessary (This is done in Results earlier)
//myMsg = myMsg.replace(/Panel Description: /g, '');

//For immunizations with a manufacturerOrganization, this piece fixes an issue where these immunizations will not display due to missing id
myMsg = myMsg.replace(/<manufacturerOrganization classCode="ORG">/g, '<manufacturerOrganization classCode="ORG"><id root="1" extension="1"/>');

//For BVCHC, converting eng to English to match receipt from ADT
myMsg = myMsg.replace(/<languageCode code="eng"/g, '<languageCode code="English"');

//For BVCHC, PCP code was not populating
myMsg = myMsg.replace(/<functionCode nullFlavor="OTH"/g, '<functionCode code="PCP"');

msg = myMsg;
}

 /**
Function to fix the missing ID of the smoking status by adding 
adding a new xml block with a new id and root extension. This lack
of a missing ID prevents ingestion of the content.
@DATE_CREATED 3/04/2016
@AUTHOR MikePinnow
@UPDATES
@DESCRIPTION    Function to fix the missing ID of the smoking status by adding 
                        adding a new xml block with a new id and root extension. This lack
                        of a missing ID prevents ingestion of the content.
@PARAM {XML} observation is an array of XML blocks that have all the observations needed to evaluate to find the tobacco usage OBS
@PARAM {int} len an integer value that is the number of XML blocks in the observation array.
@RETURN {N/A} None
*/
function fnFixMissingSocialHistoryID(observation, len) {
// [DR 4/21/16] Adding line below because I had to add a try/catch statement
default xml namespace = msg.namespace('');
// the OID needed to be added to the OBS in order for MR to ingest
// the structured data about tobacco usage
var smokingRootID = "2.16.840.1.113883.10.20.22.4.78";
var SmokingExtension = new Packages.com.mirth.connect.server.userutil.UUIDGenerator.getUUID() ;
var SmokingRoot = new Packages.com.mirth.connect.server.userutil.UUIDGenerator.getUUID() ;
var smokingNode = new XML('<id extension="'+ SmokingExtension + '" root="' + SmokingRoot + '"/>');
        // loop through all instances of observations  looking for smoking root ID
        //for (var i = 0 ; i < tmp['component']['structuredBody']['component']['section']['entry']['observation'].length(); i++ ){
        for (var i = 0 ; i < observation.length(); i++ ){
                // assign curRootID each iteration for evaluation against the smoking rootID
                // [DR 4/21/16] This fails for PPH - no 'templateId' element. Will add a try/catch
                try {
                	curRootID = observation['templateId']['@root'][i].toString();
                } catch(err) {
                	curRootID = '';
                }
                // if current root ID is for smoking then we will add the new smoking ID values to the node.
                if(curRootID == smokingRootID){
                        //appending the smokingID values to the existing node
                        observation[i]['templateId'] += smokingNode;
                }  // end if curRootID
        } // end for
}

