/**
	CJ 01/13/2016 - This function encodes a CDA for HL7 V2 processing in a OBX segment

	@param {String} msg - the CDA in it's raw form
	@return {String} return description
*/
function HL7_Encode_CDA(ccdastr) {
	var CDAWEncoding = ccdastr.toString();
	//Remove all \r\n (carriage return line feed combinations) as these would make the message appear to have extra segments
	var CDAWEncodingCRLF = CDAWEncoding.replaceAll('[\r\n]', '');
	//Replace | (pipe) with \F\ per HL7 encoding
	var CDAWEncodingPipe = CDAWEncodingCRLF.replaceAll('\\|', '\\\\F\\\\');
	//Replace ^ (carat) with \S\ per HL7 encoding
	var CDAWEncodingCarat = CDAWEncodingPipe.replaceAll('\\^', '\\\\S\\\\');
	//Replace & (ampersand) with \T\ per HL7 encoding
	var CDAWEncodingAmpersand = CDAWEncodingCarat.replaceAll('&', '\\\\T\\\\');
	//Replace ~ (tilde) with \R\ per HL7 encoding
	var CDAWEncodingTilde = CDAWEncodingAmpersand.replaceAll('~', '\\\\R\\\\');
	//Replace \n (line feed) with .br per HL7 encoding
	var CDAWEncodingLineFeed = CDAWEncodingTilde.replaceAll('[\n]', '.br');
	//Replace  (Right Single Quotation Mark) with ' (apostrophe
	var CDAWEncodingSingleQuote = CDAWEncodingLineFeed.replaceAll('’', '\'');
	ccdastr = CDAWEncodingSingleQuote;
	return ccdastr;
}

/**
	CJ 07/06/2016 - This function changes the encoding done to allow a CDA for HL7 V2 processing in a OBX segment

	@param {String} msg - the CDA in it's raw form
	@return {String} return description
*/
function HL7_Unencode_CDA(ccdastr) {
	var CDAWEncoding = ccdastr.toString();
	//Replace | (pipe) with \F\ per HL7 encoding
	var CDAWEncodingPipe = CDAWEncoding.replaceAll('\\\\F\\\\', '\\|');
	//Replace ^ (carat) with \S\ per HL7 encoding
	var CDAWEncodingCarat = CDAWEncodingPipe.replaceAll('\\\\S\\\\', '\\^');
	//Replace & (ampersand) with \T\ per HL7 encoding
	var CDAWEncodingAmpersand = CDAWEncodingCarat.replaceAll('\\\\T\\\\', '&');
	//Replace ~ (tilde) with \R\ per HL7 encoding
	var CDAWEncodingTilde = CDAWEncodingAmpersand.replaceAll('\\\\R\\\\', '~');
	//Replace \n (line feed) with .br per HL7 encoding
//	var CDAWEncodingLineFeed = CDAWEncodingTilde.replaceAll('.br', '\n');
	//Replace  (Right Single Quotation Mark) with ' (apostrophe)
	ccdastr = CDAWEncodingTilde;
	return ccdastr;
}

/**
	CJ 2/24/2016 This function will change the code, codeSystem, codeSystemname and displayName to the translation element values from the CCD if the medication code is Unknown

	@param {String} arg1 - the CDA to be modified
	@return {String} return returns a modified CDA
*/
function moveTranslationCodeIfMedicationCodeIsUnknown(arg1) {
//CJ 1/21/2016 This code will remove the Medications History Section from the CCD if the medication code is None
	var compLen = msg['component']['structuredBody']['component'].length();
//iterate through each component section
	for (var i=0;i<compLen;i++) {
//find the section with a code of 10160-0 which means the section is for Medication History
		var compCode = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
		if (compCode === '10160-0') {
//iterate through each entry of the Medications section
			var entryLen = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
			for (var j=0;j<entryLen;j++) {
//iterate through each manufacturedProduct of the entry
				var manufacturedProductLen = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct'].length();
				for (var k=0;k<manufacturedProductLen;k++) {
//iterate through each code of the manufacturedMaterial
					var manufacturedMaterialLen = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct'][k]['manufacturedMaterial'].length();
					for (var l=0;l<manufacturedMaterialLen;l++) {
						var manufacturedMaterialCodeLen = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct'][k]['manufacturedMaterial'][l]['code']['@code'].length();
						for (var m=0;m<manufacturedMaterialCodeLen;m++) {
//Find the code of the manufacturedMaterial and if it equals None then
							var manufacturedMaterialCode = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct'][k]['manufacturedMaterial'][l]['code']['@code'].toString();
							if (manufacturedMaterialCode == 'Unknown') {
//change the code, codeSystem, codeSystemname and displayName to the translation element values 
								msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct'][k]['manufacturedMaterial'][l]['code']['@code'] = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct'][k]['manufacturedMaterial'][l]['code']['translation']['@code'];
								msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct'][k]['manufacturedMaterial'][l]['code']['@codeSystem'] = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct'][k]['manufacturedMaterial'][l]['code']['translation']['@codeSystem'];
								msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct'][k]['manufacturedMaterial'][l]['code']['@codeSystemName'] = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct'][k]['manufacturedMaterial'][l]['code']['translation']['@codeSystemName'];
								msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct'][k]['manufacturedMaterial'][l]['code']['@displayName'] = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct'][k]['manufacturedMaterial'][l]['code']['translation']['@displayName'];
							}
							break;
						}
					}
				}
			}
			break;
		}
	}
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function RemoveEncFromProc(ccdastr) {
//CJ 1/21/2016 This code will remove the Immunization Section from the CCD if the only immunization code is 998
	//default xml namespace = msg.namespace('');
	default xml namespace = msg.namespace('');
	//logger.info(msg)
	var compLen = msg['component']['structuredBody']['component'].length();

	//logger.info('number of components = ' + msg['component']['structuredBody']['component'][0])
	//iterate through each component section

	for (var i=0;i<compLen;i++) 
	{

		var TranslationCode = ''+msg['component']['structuredBody']['component'][i]['section']['entry']['procedure']['code']['translation']['@code'].toString();

		if (TranslationCode == "99201" ||
		    TranslationCode == "99202" ||
		    TranslationCode == "99203" ||
		    TranslationCode == "99204" ||
		    TranslationCode == "99205" ||
		    TranslationCode == "99211" || 
		    TranslationCode == "99212" ||
		    TranslationCode == "99213" ||
		    TranslationCode == "99214" ||
		    TranslationCode == "99215" ||
		    TranslationCode == "99243") 
		{

			delete msg['component']['structuredBody']['component'][i];

			break;
		}

     }
}

/**
	CJ 1/21/2016 This function will remove the Immunization Section from the CCD if the only immunization code is 998

	@param {String} arg1 - the CDA to be modified
	@return {String} return returns a modified CDA
*/
function removeImunizationSectionIfImmunizationCodeIs998(arg1) {
//CJ 1/21/2016 This code will remove the Immunization Section from the CCD if the only immunization code is 998
	var compLen = msg['component']['structuredBody']['component'].length();
//iterate through each component section
	for (var i=0;i<compLen;i++) {
//find the section with a code of 11369-6 which means the section is for Immunizations
		var compCode = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
		if (compCode === '11369-6') {
//iterate through each entry of the Immunizations section
			var entryLen = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
			for (var j=0;j<entryLen;j++) {
//iterate through each manufacturedProduct of the entry
				var manufacturedProductLen = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct'].length();
				for (var k=0;k<manufacturedProductLen;k++) {
//iterate through each code of the manufacturedMaterial
					var manufacturedMaterialLen = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct'][k]['manufacturedMaterial'].length();
					for (var l=0;l<manufacturedMaterialLen;l++) {
						var manufacturedMaterialCodeLen = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct'][k]['manufacturedMaterial'][l]['code']['@code'].length();
						for (var m=0;m<manufacturedMaterialCodeLen;m++) {
//Find the code of the manufacturedMaterial and if it equals 998 then
							var manufacturedMaterialCode = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['consumable']['manufacturedProduct'][k]['manufacturedMaterial'][l]['code']['@code'].toString();
							if (manufacturedMaterialCode == '998') {
//delete the Immunization section
								delete msg['component']['structuredBody']['component'][i];
								break;
							}
							break;
						}
						break;
					}
					break;
				}
				break;
			}
			break;
		}
	}
}

