/**
	CJ 1/21/2016 This function will remove extraneous information in the Notes/Comments section of Immunizations

	@param {String} arg1 - the CDA to be modified
	@return {String} return returns a modified CDA
*/
function removeNotesCommentsInImmunizationSection(arg1) {
//CJ 1/21/2016 This code will remove extraneous information in the Notes/Comments section of Immunizations
	var compLen = msg['component']['structuredBody']['component'].length();
//iterate through each component section
	for (var i=0;i<compLen;i++) {
//find the section with a code of 11369-6 which means the section is for Immunization
		var compCode = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
		if (compCode === '11369-6') {
//iterate through each entry of the Immunizations section
			var entryLen = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
			for (var j=0;j<entryLen;j++) {
//iterate through each entryRelationship of the entry
				var entryRelationshipLen = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['entryRelationship'].length();
				for (var k=0;k<entryRelationshipLen;k++) {
//iterate through each Act of the entryRelationship
					var entryRelationshipActLen = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['entryRelationship'][k]['act'].length();
					for (var l=0;l<entryRelationshipActLen;l++) {
//iterate through each code of the entryRelationship
						var codeLen = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['entryRelationship'][k]['act'][l]['code']['@code'].length();
						for (var m=0;m<codeLen;m++) {
							var actCode = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['entryRelationship'][k]['act'][l]['code']['@code'].toString();
							var actCodeDisplayName = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['entryRelationship'][k]['act'][l]['code']['@displayName'].toString();
							if (actCode == '48767-8' && actCodeDisplayName == 'Immunization Registry comment') {
								delete msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['entryRelationship'][k];
							}
						}
					}
				}
			}
		}
	}
}

/**
	CJ 1/21/2016 This function will remove extra paragraph tags that are found in the narrative block of lab Results

	@param {String} arg1 - the CDA to be modified
	@return {String} return returns a modified CDA
*/
function removeParagraphTagInResultsSection(arg1) {
//CJ 1/21/2016 This code will remove extra paragraph tags that are found in the narrative block of lab Results
	var compLen = msg['component']['structuredBody']['component'].length();
//iterate through each component section
	for (var i=0;i<compLen;i++) {
//find the section with a code of 30954-2 which means the section is for Results
	var compCode = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
	var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['title'].toString();
		if (compCode === '30954-2') {
//iterate through each table of the Results section
			var tableLen = msg['component']['structuredBody']['component'][i]['section']['text']['table'].length();
			for (var j=0;j<tableLen;j++) {
//iterate through each tr of the table
				var trLen = msg['component']['structuredBody']['component'][i]['section']['text']['table'][j]['tbody']['tr'].length();
				for (var k=0;k<trLen;k++) {
//iterate through each td of the tr
					var tdLen = msg['component']['structuredBody']['component'][i]['section']['text']['table'][j]['tbody']['tr'][k]['td'].length();
					for (var l=0;l<tdLen;l++) {
//iterate through each paragraph of the td
						var paragraphLen = msg['component']['structuredBody']['component'][i]['section']['text']['table'][j]['tbody']['tr'][k]['td'][l]['paragraph'].length();
						for (var m=0;m<paragraphLen;m++) {
//change the td to not have the paragraph tag by replacing the td with the value in the paragraph
							var tdParagraph = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table'][j]['tbody']['tr'][k]['td'][l]['paragraph'].toString();
							msg['component']['structuredBody']['component'][i]['section']['text']['table'][j]['tbody']['tr'][k]['td'][l] = tdParagraph
						}
					}
				}
			}
		}
	}
}

/**
	CJ 1/21/2016 This function will remove Unknown Allergies from Machine Readable portion of CCD

	@param {String} arg1 - the CDA to be modified
	@return {String} return returns a modified CDA
*/
function removeUnknownAllergiesInAllergiesSection(arg1) {
//CJ 1/21/2016 This code will remove Unknown Allergies from Machine Readable portion of CCD
	var compLen = msg['component']['structuredBody']['component'].length();
//iterate through each component section
	for (var i=0;i<compLen;i++) {
//find the section with a code of 48765-2 which means the section is for Allergies
		var compCode = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
		if (compCode === '48765-2') {
//iterate through each entry of the Allergies section
			var entryLen = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
			for (var j=0;j<entryLen;j++) {
//iterate through each entryRelationship of the entry
				var entryRelationshipLen = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['act']['entryRelationship'].length();
				for (var k=0;k<entryRelationshipLen;k++) {
//iterate through each observation of the entryRelationship
					var observationLen = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['act']['entryRelationship'][k]['observation']['@negationInd'].length();
					for (var l=0;l<observationLen;l++) {
						var negationInd = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['act']['entryRelationship'][k]['observation']['@negationInd'].toString();
						if (negationInd == 'true') {
							delete msg['component']['structuredBody']['component'][i]['section']['entry'][j];
						}
					}
				}
			}
		}
	}
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function RemoveUnwantedProblems(ccdastr) 
{

	//default xml namespace = msg.namespace('');
	default xml namespace = msg.namespace('');
	//logger.info(msg)
	var compLen = msg['component']['structuredBody']['component'].length();


	//iterate through each component section

	for (var i=0;i<compLen;i++) 
	{
		var sectiontitile = msg['component']['structuredBody']['component'][i]['section']['title']

		if(sectiontitile == 'Problems')
		{
			//Delete the unwanted from problems table section
			var trlen = msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'].length();
			//logger.info(trlen)
			for (var t=0;t<trlen;t++) 
			{
				//var tdlen = msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][t]['td'].length();
				//for (var d=0;d<tdlen;d++) 
				//{
					//logger.info(t)
					//logger.info(msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][t]['td'][0]['content'])
					if (msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][t]['td'][0]['content'] == 'CODING COMPLETE')
					{
						delete msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][t];
						trlen -- ;
						//logger.info('deleted from problem1')
					}

				//}

			}

			//Delete the unwanted from problems so that they will nit load in CHIE
			var entryLen = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
			//logger.info(entryLen)

			for (var e=0;e<entryLen;e++) 
			{
			     //logger.info('deleted from problem2 started _ ' + e)
				var displayName = msg['component']['structuredBody']['component'][i]['section']['entry'][e]['act']['entryRelationship']['observation']['value']['translation']['@displayName']

				if(displayName == 'CODING COMPLETE')
				{
					delete msg['component']['structuredBody']['component'][i]['section']['entry'][e]
					//logger.info('deleted from problem2')
					entryLen --;

				}
			}

		}
     	if(sectiontitile == 'Encounters')
		{
			//Delete the unwanted from encounters table section
			var trlen = msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'].length();
			//logger.info(trlen)
			for (var t=0;t<trlen;t++) 
			{
				var tdlen = msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][t]['td'].length();
				for (var d=0;d<tdlen;d++) 
				{
					var listlen = msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][t]['td'][d]['list']['item'].length();
					for (var l=0;l<listlen;l++)
					{
						var itemval = msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][t]['td'][d]['list']['item'][l];
						if (itemval == 'CODING COMPLETE')
						{
						   delete (msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][t]['td'][d]['list']['item'][l]);
						   //logger.info('deleted from enc1')
						   listlen--;

						}
					}

				}

			}

			//Delete the unwanted from encounters/problems so that they will nit load in CHIE
			var entryLen = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
			//logger.info(entryLen)

			for (var e=0;e<entryLen;e++) 
			{
				var erlen = msg['component']['structuredBody']['component'][i]['section']['entry'][e]['encounter']['entryRelationship'].length();
				for (var er=0;er<erlen;er++) 
			      {
			      	//logger.info('deleted from enc2 started _ ' + er)
					var displayName  = msg['component']['structuredBody']['component'][i]['section']['entry'][e]['encounter']['entryRelationship'][er]['act']['entryRelationship']['observation']['value']['translation']['@displayName']
					if (displayName == 'CODING COMPLETE')
					{
						delete (msg['component']['structuredBody']['component'][i]['section']['entry'][e]['encounter']['entryRelationship'][er]);
						erlen--;
						//logger.info('deleted from enc2')

					}

				  }

			}

		}
		if(sectiontitile == 'Procedures')
		{
			var trlen = msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'].length();
			//logger.info(trlen)
			for (var t=0;t<trlen;t++) 
			{
				if (msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][t]['td'][0]['content'] == 'CODING COMPLETE')
					{
						delete msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][t];
						trlen -- ;
						//logger.info('deleted from procedure')
					}

			}

			//Delete the unwanted from problems so that they will nit load in CHIE
			var entryLen = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
			//logger.info(entryLen)

			for (var e=0;e<entryLen;e++) 
			{
			     //logger.info('deleted from problem2 started _ ' + e)
				var displayName = msg['component']['structuredBody']['component'][i]['section']['entry'][e]['procedure']['code']['translation']['@displayName'];

				if(displayName == 'CODING COMPLETE')
				{
					delete (msg['component']['structuredBody']['component'][i]['section']['entry'][e]);
					//logger.info('deleted from problem2')
					entryLen --;

				}
				else
				{

					var TranslationCode = msg['component']['structuredBody']['component'][i]['section']['entry'][e]['procedure']['code']['translation']['@code'];
					if(TranslationCode == "99201" ||
					TranslationCode == "99202" ||
					TranslationCode == "99203" ||
					TranslationCode == "99204" ||
					TranslationCode == "99205" ||
					TranslationCode == "99211" || 
					TranslationCode == "99212" ||
					TranslationCode == "99213" ||
					TranslationCode == "99214" ||
					TranslationCode == "99215" ||
					TranslationCode == "99243")
					{
						delete (msg['component']['structuredBody']['component'][i]['section']['entry'][e]);
						//logger.info('deleted from translation')
						entryLen --;

					}
				}
			}

		}
	}
}

/**
	Sets the visit number in PV1.19 of the HL7 message to be the ID for the first encounter in the CDA
	This is done to stop creating a duplicate encounter on the day that the CDA was generated

	@param {String} arg1 - the raw CDA to be processed
	@return {String} returns a variable called visitNumber which can be used in PV1.19
*/
function setVisitNumberToFirstEncounterId(arg1) {
// ENCOUNTERS ***********************
default xml namespace = msg.namespace('');
try {
	var numComps = msg['component']['structuredBody']['component'].length();
} catch(err) {
	var numComps = 0;
}
for (var i=0;i<numComps;i++) {
	try {
		var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
	} catch(err) {
		var compTitle = '';
	}
	if (compTitle.length == 0) {
		try {
			var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['title'].toString();
		} catch(err) {
			var compTitle = '';
		}
	}	
 	if ((compTitle == '46240-8') || compTitle.toUpperCase().search("ENCOUNTERS") != -1) {
 		//create a variable called visitNumber that is set as the ID for the first encounter
		try {
			var visitNumber = msg['component']['structuredBody']['component'][i]['section']['entry'][0]['encounter']['id']['@extension'].toString();
			channelMap.put('visitNumber',visitNumber)
		} catch(err) {
			var sectEntryCount = 0;
		}
 	}
}
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function substituteMRpvID(dataSource) {
default xml namespace = msg.namespace('');	
// Substitute the MirthResultsID for the provider UUID so Mirth won't create a new Provider if first and last already in MR for this facility
if (dataSource.length > 0) {
	var dbConn = getMRDBConnection_CCD_PVID();
	var numProv = msg['documentationOf']['serviceEvent']['performer'].length();
	for (var i=0;i<numProv;i++) {
		try {
			var pLast = ''+msg['documentationOf']['serviceEvent']['performer'][i]['assignedEntity']['assignedPerson']['name']['family'].toString();
		} catch(err) {
			var pLast = '';
		}
		try {	
			var pFirst = ''+msg['documentationOf']['serviceEvent']['performer'][i]['assignedEntity']['assignedPerson']['name']['given'].toString();
		} catch(err) {
			var pFirst = '';
		}
		if (pLast.length > 0 && pFirst.length > 0) {
			var getMRID = "select DISTINCT pa.alias from provider p, name n, provider_alias pa, facility f ";
			getMRID += "where p.name_key = n.name_key and upper(n.last) = '"+pLast.toUpperCase()+"' and upper(n.first) = '"+pFirst.toUpperCase()+"' and ";
			getMRID += "p.provider_key = pa.provider_key and pa.facility_key = f.facility_key and f.short_label = '"+dataSource+"'";
			var result = dbConn.executeCachedQuery(getMRID);
			var pvID = '';
			while (result.next()) {
				pvID = ''+result.getString('alias');
			}
			if (pvID.length != 0) {
				msg['documentationOf']['serviceEvent']['performer'][i]['assignedEntity']['id']['@extension'] = pvID;
			}
		}
	}
}
}

