function updateAdvDir_NG(arg1) {
// ADVANCE DIRECTIVE **************************
default xml namespace = msg.namespace('');
try {
	var numComps = msg['component']['structuredBody']['component'].length();
} catch(err) {
	var numComps = 0;
}
for (var i=0;i<numComps;i++) {
	try {
		var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
	} catch(err) {
		var compTitle = '';
	}
	if (compTitle.length == 0) {
		try {
			var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['title'].toString();
		} catch(err) {
			var compTitle = '';
		}
	}
	if ((compTitle == '42348-3') || compTitle.toUpperCase().search('ADVANCE DIRECTIVE') != -1) {
		// XML: Remove the component
		delete msg['component']['structuredBody']['component'][i];
		break;
	}
}

