/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function updateDocs_NG(dataSource) {
// Change the Title
default xml namespace = msg.namespace('');
var custName = ''+msg['custodian']['assignedCustodian']['representedCustodianOrganization']['name'].toString();
if (custName.length > 0) {
	msg['title'] = 'Continuity of Care Document - '+custName;
}
// Remove any duplicate secondary providers and providers with no last name
var done = false;
var paranoiaCount = 0;
while (!done) {
	paranoiaCount++;
	var numProv = msg['documentationOf']['serviceEvent']['performer'].length();
	for (var i=0;i<numProv;i++) {
		try {
			var pLast = ''+msg['documentationOf']['serviceEvent']['performer'][i]['assignedEntity']['assignedPerson']['name']['family'].toString();
		} catch(err) {
			var pLast = '';
		}
		try {
			var pFirst = ''+msg['documentationOf']['serviceEvent']['performer'][i]['assignedEntity']['assignedPerson']['name']['given'].toString();
		} catch(err) {
			var pFirst = '';
		}	
		pFirst = pFirst.toUpperCase();
		pLast = pLast.toUpperCase();		
		var segDeleted = false;
		if (pLast.length == 0) {
			segDeleted = true;
			delete msg['documentationOf']['serviceEvent']['performer'][i];
			break;			
		} else {
			for (var j=0;j<numProv;j++) {
				if (j == i) {continue;}
				try {
					var npLast = ''+msg['documentationOf']['serviceEvent']['performer'][j]['assignedEntity']['assignedPerson']['name']['family'].toString();
				} catch(err) {
					var npLast = '';
				}
				try {
					var npFirst = ''+msg['documentationOf']['serviceEvent']['performer'][j]['assignedEntity']['assignedPerson']['name']['given'].toString();
				} catch(err) {
					var npFirst = '';
				}			
				try {
					var isPCP = ''+msg['documentationOf']['serviceEvent']['performer'][j]['functionCode']['@code'].toString();
				} catch(err) {
					var isPCP = '';
				}			
				if (npFirst.toUpperCase() == pFirst && npLast.toUpperCase() == pLast && isPCP.toUpperCase() != 'PCP' && isPCP.toUpperCase() != 'PP') {
					segDeleted = true;
					delete msg['documentationOf']['serviceEvent']['performer'][j];
					break;
				}
			}
		}
	}
	if (numProv < 2) {
		done = true;
	}
	if (!segDeleted) {
		done = true;
	}
	if (paranoiaCount > 100) {
		done = true;
	}	
}	
substituteMRpvID(dataSource);
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function updateEncounters_NG(arg1) {
// ENCOUNTERS ***********************
default xml namespace = msg.namespace('');
try {
	var numComps = msg['component']['structuredBody']['component'].length();
} catch(err) {
	var numComps = 0;
}
for (var i=0;i<numComps;i++) {
	try {
		var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
	} catch(err) {
		var compTitle = '';
	}
	if (compTitle.length == 0) {
		try {
			var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['title'].toString();
		} catch(err) {
			var compTitle = '';
		}
	}	
 	if ((compTitle == '46240-8') || compTitle.toUpperCase().search("ENCOUNTERS") != -1) {
 		//change to convert all encounter types from Unknown to Outpatient
		try {
			var sectEntryCount = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
		} catch(err) {
			var sectEntryCount = 0;
		}
		for (var j=0;j<sectEntryCount;j++)  {                                         
			try {
				var isNull = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['encounter']['code']['@nullFlavor'].toString();
				if (isNull == 'UNK') {
					msg['component']['structuredBody']['component'][i]['section']['entry'][j]['encounter']['code']['@nullFlavor'] = 'O';
				}
			} catch(err) {
				var isNull = '';
			}
		}          		
 	}
}
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function updateFamilyHistory_MP(arg1) {
default xml namespace = msg.namespace('');

// FAMILY HISTORY
try {
	var numComps = msg['component']['structuredBody']['component'].length();
} catch(err) {
	var numComps = 0;
}
for (var i=0;i<numComps;i++) {
	try {
		var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
	} catch(err) {
		var compTitle = '';
	}
	if (compTitle.length == 0) {
		try {
			var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['title'].toString();
		} catch(err) {
			var compTitle = '';
		}
	}
	if ((compTitle == '10157-6') || compTitle.toUpperCase() == 'FAMILY HISTORY') {
		// Remove any entries that have no code or text and add "Unspecified" if there is a code or text, but the family member is blank
		// MR won't display entry if there is no family member attached to it
		var done = false;
		var paranoiaCount = 0;
		while (!done) {
			paranoiaCount ++;
			try {
				var numRows = msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'].length();
			} catch(err) {
				var numRows = 0;
			}
			var foundEmptyLine = false;
			for (var j=0;j<numRows;j++) {
				try {
					var famMember = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][0].toString();
				} catch(err) {
					var famMember = '';
				}
				try {
					var codeVal = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][1].toString();
				} catch(err) {
					var codeVal = '';
				}
				try {
					var textVal = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][2].toString();
				} catch(err) {
					var textVal = '';
				}
				if (codeVal.length == 0 && textVal.length == 0) {
					delete msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j];
					foundEmptyLine = true;
					break;							
				}
				if (famMember.length == 0) {
					msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][0] = 'Unspecified';
				}
			}
			if (foundEmptyLine) {
				done = false;
			} else if (j >= numRows) {
				done = true;
			}
			if (paranoiaCount > 100) {
				done = true;
			}
		}
		// Remove any blank entries from the XML so they don't show up in MR
		var done = false;
		var paranoiaCount = 0;
		while (!done) {
			paranoiaCount ++;
			try {
				var numEntries = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
			} catch(err) {
				var numEntries = 0;
			}
			var foundEmptyLine = false;
			for (var j=0;j<numEntries;j++) {
				try {
					var codeVal = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['organizer']['component']['observation']['value']['@nullFlavor'].toString();
				} catch(err) {
					var codeVal = '';
				}
				if (codeVal.toUpperCase() == 'NI') {
					delete msg['component']['structuredBody']['component'][i]['section']['entry'][j];
					foundEmptyLine = true;
					break;							
				} else {
					try {
						var textVal = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['organizer']['component']['observation']['value']['translation']['@displayName'].toString();
					} catch(err) {
						var textVal = '';
					}
					if (textVal == 'UNK') {
						delete msg['component']['structuredBody']['component'][i]['section']['entry'][j];
						foundEmptyLine = true;
						break;	
					}
				}
			}
			if (foundEmptyLine) {
				done = false;
			} else if (j >= numEntries) {
				done = true;
			}
			if (paranoiaCount > 100) {
				done = true;
			}
		}
		// Overwrite the code with the text of the condition; otherwise they are all "Problem"
		try {
			var numEntries = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
		} catch(err) {
			var numEntries = 0;
		}
		for (var j=0;j<numEntries;j++) {
			try {
				var textVal = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['organizer']['component']['observation']['value']['translation']['@displayName'].toString();
			} catch(err) {
				var textVal = '';
			}
			if (textVal.length > 0) {
				try {
					msg['component']['structuredBody']['component'][i]['section']['entry'][j]['organizer']['component']['observation']['code']['@displayName'] = textVal;
					msg['component']['structuredBody']['component'][i]['section']['entry'][j]['organizer']['component']['observation']['code']['@code'] = textVal;					
				} catch(err) {
					//skip it
				}
			}
		}		
		break;
	}
}
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function updateFamilyHistory_NG(arg1) {
// FAMILY HISTORY ***********************
default xml namespace = msg.namespace('');
try {
	var numComps = msg['component']['structuredBody']['component'].length();
} catch(err) {
	var numComps = 0;
}
for (var i=0;i<numComps;i++) {
	try {
		var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
	} catch(err) {
		var compTitle = '';
	}
	if (compTitle.length == 0) {
		try {
			var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['title'].toString();
		} catch(err) {
			var compTitle = '';
		}
	}	
 	if ((compTitle == '10157-6') || compTitle.toUpperCase().search("FAMILY HISTORY") != -1) {
 		// HTML: Remove the row if there is nothing in the Diagnosis column	
		var done = false;
		var paranoiaCount = 0;
		while (!done) {
			paranoiaCount ++;
			try {
				var numRows = msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'].length();
			} catch(err) {
				var numRows = 0;
			}
			var foundEmptyLine = false;
			for (var j=0;j<numRows;j++) {
				try {
					var textVal = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][1]['content'].toString();
				} catch(err) {
					var textVal = '';
				}
				if (textVal.length == 0) {
					delete msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j];
					foundEmptyLine = true;
					break;							
				}
			}
			if (foundEmptyLine) {
				done = false;
			} else if (j >= numRows) {
				done = true;
			}
			if (paranoiaCount > 100) {
				done = true;
			}
		} 			
		// HTML: Remove the header Status column, per NextGen.	 
 		try {
 			delete msg['component']['structuredBody']['component'][i]['section']['text']['table']['thead']['tr']['th'][3];
 		} catch(err) {
 			// skip it
 		} 			
 		// HTML: Make a list of the reference variables for later substitution in the XML.
 		try {
 			var numtrows = msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'].length();
 		} catch(err) {
 			var numtrows = 0;
 		}
 		var trowArr_famMem = new Array();
 		var trowArr_name = new Array();
 		var trowArr_ref = new Array();
 		for (var j=0;j<numtrows;j++) {	
 			try {
 				trowArr_famMem[j] = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][0]['content'].toString();
 				if (trowArr_famMem[j].length == 0) {
 					trowArr_famMem[j] = 'Unspecified';
 				}
 			} catch(err) {
 				trowArr_famMem[j] = 'Unspecified';
 			}
 			try {
 				trowArr_name[j] = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][1]['content'].toString().replace(/[\"]/g, '');
 			} catch(err) {
 				trowArr_name[j] = '';
 			}
 			try {
 				trowArr_ref[j] = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][1]['content']['@ID'].toString();
 			} catch(err) {
 				trowArr_ref[j] = '';
 			}
 			// HTML: Remove the Status column, per NextGen.
 			try {
 				delete msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][3];
 			} catch(err) {
 				// skip it
 			} 			
 		}
 		try {
 			var numentries = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
 		} catch(err) {
 			var numentries = 0;
 		}
 		for (var k=0;k<numentries;k++) {
			// XML: Remove the entry if it was removed in the HTML
			var done = false;
			var paranoiaCount = 0;
			while (!done) {
				paranoiaCount ++;
 				try {
 					var numEntryComps = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'].length();
 				} catch(err) {
 					var numEntryComps = 0;
 				} 
 				for (var l=0;l<numEntryComps;l++) {
					try {
 						var entryRef = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'][l]['observation']['value']['originalText']['reference']['@value'].toString();
 					} catch(err) {
 						var entryRef = '';
 					}
 					if (entryRef.length > 2) {
 						var foundOrphanComp = true;
 						entryRef = entryRef.substr(1);				
						for (var j=0;j<numtrows;j++) {
 							var trowref = ''+trowArr_ref[j];							
 							if (trowref.toUpperCase() == entryRef.toUpperCase()) {
 								foundOrphanComp = false;
 								break;
 							}
						}
						if (foundOrphanComp) {
							delete msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'][l];
							break;		
						}
 					} else {
 						foundOrphanComp = false;
 					}
 				}
				if (foundOrphanComp) {
					done = false;
				} else {
					done = true;
				}
				if (paranoiaCount > 100) {
					done = true;
				}
 			} 		  			
 			// XML: Overwrite "Diagnosis" with the reference variable from the HTML section.
 			try {
 				var numEntryComps = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'].length();
 			} catch(err) {
 				var numEntryComps = 0;
 			} 			
 			for (var l=0;l<numEntryComps;l++) {
 				try {
 					var entryRef = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'][l]['observation']['value']['originalText']['reference']['@value'].toString();
 				} catch(err) {
 					var entryRef = '';
 				}
 				if (entryRef.length > 2) {
 					entryRef = entryRef.substr(1);
 				}	
 				for (var j=0;j<numtrows;j++) {
 					var trowfamMem = ''+trowArr_famMem[j];
 					var trowname = ''+trowArr_name[j];
 					var trowref = ''+trowArr_ref[j];
 					if (trowref.toUpperCase() == entryRef.toUpperCase()) {
 						try {
 							msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'][l]['observation']['code']['@displayName'] = trowname;
  						} catch(err) {
 							// skip it
 						}
 						try {
  							msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'][l]['observation']['code']['@code'] = trowname;
 						} catch(err) {
 							// skip it
 						} 					
 						try {
 							msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'][l]['observation']['value']['originalText']['reference']['@value'] = trowname;
 						} catch(err) {
 							// skip it
 						} 	
 						// XML: Overwrite "Subject" (family member) with the reference variable from the HTML section.
 						try {
 							var subj = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['subject']['relatedSubject']['code']['@nullFlavor'].toString();
 						} catch(err) {
 							var subj = ''; 				
 						}
 						if (subj.toUpperCase() == 'UNK') {
							msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['subject']['relatedSubject']['code']['@nullFlavor'] = "AXXN8"+trowfamMem;
							msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['subject']['relatedSubject']['code']['@displayName'] = trowfamMem;
 						}
 						break;
 					}
 				}
 				// The following was supplied by NG, but I tested it and it doesn't seem to work
				//var repstring =  ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'][l]['observation'].toString();
			 	//msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'][l]['observation'] = repstring.replace(/valuep/g, 'code'); 				
 			} 						
 		}		
 		break;
 	}
}
}

