/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function updateMeds_MP(arg1) {
default xml namespace = msg.namespace('');	
// MEDICATIONS
try {
	var numComps = msg['component']['structuredBody']['component'].length();
} catch(err) {
	var numComps = 0;
}
for (var i=0;i<numComps;i++) {
	try {
		var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
	} catch(err) {
		var compTitle = '';
	}
	if (compTitle.length == 0) {
		try {
			var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['title'].toString();
		} catch(err) {
			var compTitle = '';
		}
	}
	if ((compTitle == '10160-0') || compTitle.toUpperCase() == 'MEDICATIONS') {
		// Remove any blank entries
		try {
			var numEntries = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
		} catch(err) {
			var numEntries = 0;
		}
		if (numEntries == 1) {
			try {
				var textVal = ''+msg['component']['structuredBody']['component'][i]['section']['entry']['substanceAdministration']['consumable']['manufacturedProduct']['manufacturedMaterial']['code']['@displayName'].toString();
			} catch(err) {
				var textVal = '';
			}
			try {
				var codeVal = ''+msg['component']['structuredBody']['component'][i]['section']['entry']['substanceAdministration']['consumable']['manufacturedProduct']['manufacturedMaterial']['code']['@code'].toString();
			} catch(err) {
				var codeVal = '';
			}			
			if (textVal.toUpperCase() == 'UNK' && codeVal.toUpperCase() == 'UNK') {
				delete msg['component']['structuredBody']['component'][i];
				break;
			}
		}
		break;
	}
}
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function updateMeds_NG(arg1) {
// MEDICATIONS ***************************
default xml namespace = msg.namespace('');
try {
	var numComps = msg['component']['structuredBody']['component'].length();
} catch(err) {
	var numComps = 0;
}
for (var i=0;i<numComps;i++) {
	try {
		var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
	} catch(err) {
		var compTitle = '';
	}
	if (compTitle.length == 0) {
		try {
			var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['title'].toString();
		} catch(err) {
			var compTitle = '';
		}
	}


	if ((compTitle == '10160-0') || compTitle.toUpperCase().search('MEDICATIONS') != -1) {
		// HTML: Remove the trailing ' - ' from the Start-Stop date range when there is no Stop date
		try {
			var numRows = msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'].length();
		} catch(err) {
			var numRows = 0;
		}
		for (var t=0;t<numRows;t++) {
			try {
				var dateRange = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][t]['td'][3]['content'].toString();
			} catch(err) {
				var dateRange = '';
			}
			var dateLen = dateRange.length;
			if (dateLen > 2) {
				var last3Indx = dateLen-3;
				var last3 = dateRange.substr(last3Indx,3);
				if (last3 == ' - ') {
					msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][t]['td'][3]['content'] = dateRange.substring(0,last3Indx);
				}
			}
			try {
				var dateRange = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][t]['td'][3].toString();
			} catch(err) {
				var dateRange = '';
			}
			var dateLen = dateRange.length;
			if (dateLen > 2) {
				var last3Indx = dateLen-3;
				var last3 = dateRange.substr(last3Indx,3);
				if (last3 == ' - ') {
					msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][t]['td'][3] = dateRange.substring(0,last3Indx);
				}
			}			
		}
		// XML: Remove component if there's only 1 blank entry
		try {
			var numEntries = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
		} catch(err) {
			var numEntries = 0;
		}
		if (numEntries == 1) {
			try {
				var codeVal = ''+msg['component']['structuredBody']['component'][i]['section']['entry']['substanceAdministration']['consumable']['manufacturedProduct']['manufacturedMaterial']['code']['@nullFlavor'].toString();
			} catch(err) {
				var codeVal = '';
			}

			if (codeVal == 'NI') {
				delete msg['component']['structuredBody']['component'][i];
				break;
			}
		}		
		break;
	}
}
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function updatePlanOfCare_MP(arg1) {
default xml namespace = msg.namespace('');
//PLAN OF CARE
try {
	var numComps = msg['component']['structuredBody']['component'].length();
} catch(err) {
	var numComps = 0;
}
for (var i=0;i<numComps;i++) {
	try {
		var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
	} catch(err) {
		var compTitle = '';
	}
	if (compTitle.length == 0) {
		try {
			var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['title'].toString();
		} catch(err) {
			var compTitle = '';
		}
	}
	if ((compTitle == '18776-5') || compTitle.toUpperCase() == 'PLAN OF CARE') {
		// HTML: Remove the substance (meds) entries from Plan of Care. They are also under Medications and create duplicates
		try {
 			var numentries = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
 		} catch(err) {
 			var numentries = 0;
 		}
 		for (var k=0;k<numentries;k++) {
			var done = false;
			var paranoiaCount = 0;
			while (!done) {
				paranoiaCount ++;
 				try {
 					var numEntryComps = msg['component']['structuredBody']['component'][i]['section']['entry'][k]['substanceAdministration'].length();
 				} catch(err) {
 					var numEntryComps = 0;
 				} 
 				var deletedSeg = false;
 				for (var l=0;l<numEntryComps;l++) {
					try {
 						var codeVal = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['substanceAdministration'][l]['consumable']['manufacturedProduct']['manufacturedMaterial']['code']['@code'].toString();
 					} catch(err) {
 						var codeVal = '';
 					}
 					if (codeVal.toUpperCase() == 'UNK') {
						delete msg['component']['structuredBody']['component'][i]['section']['entry'][k]['substanceAdministration'][l];
						deletedSeg = true;
						break;		
 					} 
 				}
				if (deletedSeg) {
					done = false;
				} else {
					done = true;
				}
				if (paranoiaCount > 100) {
					done = true;
				}
 			} 		  		
 		}
		break;
	}
}
}

/**
	This function cleans up the Plan of Care section for NextGen messages

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function updatePlanOfCare_NG(arg1) {
// PLAN OF CARE ***********************
default xml namespace = msg.namespace('');
try {
	var numComps = msg['component']['structuredBody']['component'].length();
} catch(err) {
	var numComps = 0;
}
for (var i=0;i<numComps;i++) {
	try {
		var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
	} catch(err) {
		var compTitle = '';
	}
	if (compTitle.length == 0) {
		try {
			var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['title'].toString();
		} catch(err) {
			var compTitle = '';
		}
	}	
 	if ((compTitle == '18776-5') || compTitle.toUpperCase().search("PLAN OF CARE") != -1) {
		try {
			var sectEntryCount = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
		} catch(err) {
			var sectEntryCount = 0;
		}
		sectEntryCount--;
 		//remove future appts -- they create completely blank Inpatient Encounters
		for (var j=sectEntryCount;j>=0;j--)  {                                         
			try {
				var isEnc = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['encounter']['@classCode'].toString();
			} catch(err) {
				var isEnc = '';
			}
			if (isEnc.toUpperCase() == 'ENC') {
				delete msg['component']['structuredBody']['component'][i]['section']['entry'][j];
			}
		}          		
 		//remove sbadm entries -- they create completely blank Medications
		for (var j=sectEntryCount;j>=0;j--)  {                                         
			try {
				var isSdbAdm = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['substanceAdministration']['@classCode'].toString();
			} catch(err) {
				var isSdbAdm = '';
			}
			if (isSdbAdm.toUpperCase() == 'SBADM') {
				delete msg['component']['structuredBody']['component'][i]['section']['entry'][j];
			}
		}          		
 	}
}
}

