/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function updateProblems_NG(arg1) {
// PROBLEMS ********************************
default xml namespace = msg.namespace('');
try {
	var numComps = msg['component']['structuredBody']['component'].length();
} catch(err) {
	var numComps = 0;
}
for (var i=0;i<numComps;i++) {
	try {
		var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
	} catch(err) {
		var compTitle = '';
	}
	if (compTitle.length == 0) {
		try {
			var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['title'].toString();
		} catch(err) {
			var compTitle = '';
		}
	}

	if ((compTitle == '11450-4') || compTitle.toUpperCase().search('PROBLEMS') != -1) {
		// HTML: Remove the row if there is no Condition identified
		var done = false;
		var paranoiaCount = 0;
		while (!done) {
			paranoiaCount ++;
			try {
				var numRows = msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'].length();
			} catch(err) {
				var numRows = 0;
			}
			var foundEmptyLine = false;
			for (var j=0;j<numRows;j++) {
				try {
					var textVal = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][0]['content'].toString();
				} catch(err) {
					var textVal = '';
				}
				if (textVal.length == 0) {
					delete msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j];
					foundEmptyLine = true;
					break;							
				}
			}
			if (foundEmptyLine) {
				done = false;
			} else if (j >= numRows) {
				done = true;
			}
			if (paranoiaCount > 100) {
				done = true;
			}
		}
		// HTML: Remove the trailing ' - ' from the Start-Stop date range when there is no Stop date
		try {
			var numRows = msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'].length();
		} catch(err) {
			var numRows = 0;
		}
		for (var t=0;t<numRows;t++) {
			try {
				var dateRange = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][t]['td'][1]['content'].toString();
			} catch(err) {
				var dateRange = '';
			}
			var dateLen = dateRange.length;
			if (dateLen > 2) {
				var last3Indx = dateLen-3;
				var last3 = dateRange.substr(last3Indx,3);
				if (last3 == ' - ') {
					msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][t]['td'][1]['content'] = dateRange.substring(0,last3Indx);
				}
			}
			try {
				var dateRange = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][t]['td'][1].toString();
			} catch(err) {
				var dateRange = '';
			}
			var dateLen = dateRange.length;
			if (dateLen > 2) {
				var last3Indx = dateLen-3;
				var last3 = dateRange.substr(last3Indx,3);
				if (last3 == ' - ') {
					msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][t]['td'][1] = dateRange.substring(0,last3Indx);
				}
			}			
		}
		break;
	}
}
