/**
	@param {String} arg1 - the CDA to be modified
	@return {String} return returns a modified CDA
*/
function updateResults_MP(arg1) {
default xml namespace = msg.namespace('');
//LAB RESULTS
try {
	var numComps = msg['component']['structuredBody']['component'].length();
} catch(err) {
	var numComps = 0;
}
for (var i=0;i<numComps;i++) {
	try {
		var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
	} catch(err) {
		var compTitle = '';
	}
	if (compTitle.length == 0) {
		try {
			var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['title'].toString();
		} catch(err) {
			var compTitle = '';
		}
	}
	if ((compTitle == '30954-2') || compTitle.search('LAB RESULTS') != -1) {
		// Remove any blank entries
		try {
			var numEntries = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
		} catch(err) {
			var numEntries = 0;
		}
		if (numEntries == 1) {
			try {
				var textVal = ''+msg['component']['structuredBody']['component'][i]['section']['entry']['organizer']['component']['observation']['code']['@displayName'].toString();
			} catch(err) {
				var textVal = '';
			}
			try {
				var codeVal = ''+msg['component']['structuredBody']['component'][i]['section']['entry']['organizer']['component']['observation']['code']['@code'].toString();
			} catch(err) {
				var codeVal = '';
			}			
			if (textVal.toUpperCase() == 'UNK' && codeVal.toUpperCase() == 'UNK') {
				delete msg['component']['structuredBody']['component'][i];
				break;
			}
		}
		// Some values come in thru the originalText element, not the value element (e.g., 'negative')
		for (var e=0;e<numEntries;e++) {
			try {
				var numComps = msg['component']['structuredBody']['component'][i]['section']['entry'][e]['organizer']['component'].length();
			} catch(err) {
				var numComps = 0;
			}			
			for (var c=0;c<numComps;c++) {
				try {
					var isValue = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][e]['organizer']['component'][c]['observation']['value']['@value'].toString();
				} catch(err) {
					var isValue = '';	
				}
				if (isValue.length == 0) {
					try {
						var origText = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][e]['organizer']['component'][c]['observation']['value']['originalText'].toString();
					} catch(err) {
						var origText = '';
					}
					if (origText.length > 0) {
						msg['component']['structuredBody']['component'][i]['section']['entry'][e]['organizer']['component'][c]['observation']['value']=origText;						
						msg['component']['structuredBody']['component'][i]['section']['entry'][e]['organizer']['component'][c]['observation']['value']['@xsi:type']='ST';
					}
				}
			}
		}
		break;
	}
}
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function updateResults_NG(arg1) {
// RESULTS ***********************
default xml namespace = msg.namespace('');
try {
	var numComps = msg['component']['structuredBody']['component'].length();
} catch(err) {
	var numComps = 0;
}
for (var i=0;i<numComps;i++) {
	try {
		var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
	} catch(err) {
		var compTitle = '';
	}
	if (compTitle.length == 0) {
		try {
			var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['title'].toString();
		} catch(err) {
			var compTitle = '';
		}
	}	
 	if ((compTitle == '30954-2') || compTitle.toUpperCase().search("RESULTS") != -1) {
		// Get rid of it if there are no results
		try {
			var numEntries = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
		} catch(err) {
			var numEntries = 0;
		}
		var sectDeleted = false;
		var numCompObs = 0;
		var codeVal = '';
		if (numEntries == 1) {
			try {
				var numComps = msg['component']['structuredBody']['component'][i]['section']['entry']['organizer']['component'].length();
			} catch(err) {
				var numComps = 0;
			}			
			for (var j=0;j<numComps;j++) {
				var numObs = msg['component']['structuredBody']['component'][i]['section']['entry']['organizer']['component'][j]['observation'].length();
				if (numObs == 1) {
					try {
						codeVal = ''+msg['component']['structuredBody']['component'][i]['section']['entry']['organizer']['component'][j]['observation']['code']['@code'].toString();
					} catch(err) {
						codeVal = '';
					}	
				}
				numCompObs = numCompObs+numObs;
			}
			if (numCompObs <= 1) {
				if (codeVal.length == 0 || codeVal.toUpperCase() == 'UNK') {
					delete msg['component']['structuredBody']['component'][i];
					sectDeleted = true;
					break;
				}				
			}
			if (sectDeleted) {
				break;
			}
		}	 		
		// HTML: Make a list of the reference variables for later substitution in the XML.
 		try {
 			var numtrows = msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'].length();
 		} catch(err) {
 			var numtrows = 0;
 		}
 		var trowArr_name = new Array();
 		var trowArr_ref = new Array();
 		var trowArr_commentref = new Array();
 		var trowArr_comment = new Array();
 		for (var j=0;j<numtrows;j++) {
 			try {
 				var thisRef = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][0]['content']['@ID'].toString();
 			} catch(err) {
 				var thisRef = '';
 			}
 			try {
				var thisName = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][0]['content'].toString(); 			
 			} catch(err) {
 				var thisName = '';
 			}	
 			try {
 				var thiscommentRef = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][6]['content']['@ID'].toString();
 			} catch(err) {
 				var thiscommentRef = '';
 			} 			
 			try {
				var thisComment = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][6]['content'].toString(); 			
 			} catch(err) {
 				var thisComment = '';
 			}	
			// Remove "Panel Description: " text
 			if (thisName.toUpperCase().search("PANEL DESCRIPTION: ") != -1) {
 				thisName = thisName.replace(/Panel Description: /i, '');
 				msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][0]['content'] = thisName; 					
 			} 	
 			trowArr_ref[j] = thisRef;
 			trowArr_name[j] = thisName;	
 			trowArr_commentref[j] = thiscommentRef;
 			trowArr_comment[j] = thisComment;
 		}
		// XML: Overwrite "panel" with the reference variable from the HTML section.
 		try {
 			var numentries = msg['component']['structuredBody']['component'][i]['section']['entry'].length();

 		} catch(err) {
 			var numentries = 0;
 		}
 		for (var k=0;k<numentries;k++) { 	
 			try {
 				var entryRef = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['code']['originalText']['reference']['@value'].toString();
 			} catch(err) {
 				var entryRef = '';
 			}
 			if (entryRef.length > 2) {
 				entryRef = entryRef.substr(1);
 			}				
 			for (var j=0;j<numtrows;j++) {
 				var trowname = ''+trowArr_name[j];
 				var trowref = ''+trowArr_ref[j]; 				
 				if ((trowref.toUpperCase() == entryRef.toUpperCase()) && trowname.length > 0 && trowname.toUpperCase() != 'UNKNOWN') {					
 					try {
 						msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['code']['@code'] = trowname;
  					} catch(err) {
 						// skip it
 					}									
 					break;
 				}
 			} 				
 		}
 		// XML: Overwrite "result code" with the reference variable from the HTML section.
 		try {
 			var numentries = msg['component']['structuredBody']['component'][i]['section']['entry'].length();

 		} catch(err) {
 			var numentries = 0;
 		}
 		for (var k=0;k<numentries;k++) { 	
 			try {
 				var panel = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['code']['@code'].toString();
 			} catch(err) {
 				var panel = '';
 			} 	 		
 			try {
 				var numEntryComps = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'].length();
 			} catch(err) {
 				var numEntryComps = 0;
 			} 			
 			for (var l=0;l<numEntryComps;l++) {
 				try {
 					var entryRef = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'][l]['observation']['text']['reference']['@value'].toString();
 				} catch(err) {
 					var entryRef = '';
 				}
 				if (entryRef.length > 2) {
 					entryRef = entryRef.substr(1);
 				} else {
 					continue;
 				}
 				try {
 					var entryCommentRef = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'][l]['observation']['entryRelationship']['act']['text']['reference']['@value'].toString();
 				} catch(err) {
 					var entryCommentRef = '';
 				} 	
 				if (entryCommentRef.length > 2) {
 					entryCommentRef = entryCommentRef.substr(1);
 				}
 				var result = '';
 				for (var j=0;j<numtrows;j++) {
 					var trowname = ''+trowArr_name[j];
 					var trowref = ''+trowArr_ref[j];
 					var trowcomment = ''+trowArr_comment[j];
 					var trowcommentref = ''+trowArr_commentref[j]; 					
 					if (trowref.toUpperCase() == entryRef.toUpperCase() && trowname.toUpperCase() != 'UNKNOWN') {
 						result = trowname;
 						try {
 							msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'][l]['observation']['code']['@displayName'] = trowname;
  						} catch(err) {
 							// skip it
 						}
 						try {
  							msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'][l]['observation']['code']['@code'] = trowname;
 						} catch(err) {
 							// skip it
 						} 	
 						if (trowcommentref.toUpperCase() == entryCommentRef.toUpperCase() && entryCommentRef.length > 0) {
 							try {
 								var curComment = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'][l]['observation']['value'].toString();
 								if (curComment.length == 0 || curComment.toUpperCase() == 'COMMENT') {
 									msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'][l]['observation']['value'] = trowcomment;
 									msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'][l]['observation']['value']['@xsi:type'] = 'ST';
 								}
  							} catch(err) {
 								// skip it
 							}
 						}
 						break;
 					}
 				}
 				// XML: MR requires a non-NULL ID in order to display the item.
				try {
					var isNull = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'][l]['observation']['id']['@nullFlavor'].toString();
				} catch(err) {
					var isNull = '';
				} 				
				if (isNull == 'UNK') {
					try {
						var ts = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['effectiveTime']['@value'].toString();
					} catch(err) {
						var ts = '';
					}
					var newID = ''+panel+result+ts;
					newID = newID.replace(/\s/g,'');
					if (newID.length > 0) {
						msg['component']['structuredBody']['component'][i]['section']['entry'][k]['organizer']['component'][l]['observation']['id']['@nullFlavor'] = newID;
					}
				}
 			}
 		}
 		break;
 	}
}
}

