/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function updateSocialHistory_MP(arg1) {
default xml namespace = msg.namespace('');
// SOCIAL HISTORY
try {
	var numComps = msg['component']['structuredBody']['component'].length();
} catch(err) {
	var numComps = 0;
}
for (var i=0;i<numComps;i++) {
	try {
		var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
	} catch(err) {
		var compTitle = '';
	}
	if (compTitle.length == 0) {
		try {
			var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['title'].toString();
		} catch(err) {
			var compTitle = '';
		}
	}
	if ((compTitle == '29762-2') || compTitle.toUpperCase() == 'SOCIAL HISTORY') {
		try {
			var numEntries = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
		} catch(err) {
			var numEntries = 0;
		}
		for (var j=0;j<numEntries;j++) {
			// Overwrite the numeric codes with the text to make it more human readable
			try {
				var textVal = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['text'].toString();
			} catch(err) {
				var textVal = '';
			}
			if (textVal.length > 0) {
				try {
					msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['code']['@code'] = textVal;
				} catch(err) {
					//skip it
				}
			}
			try {
				var codSystemDisplName = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['code']['@codeSystemName'].toString();
			} catch(err) {
				var codSystemDisplName = '';
			}
			if (codSystemDisplName.length > 0) {
				try {
					msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['code']['@codeSystem'] = codSystemDisplName;
				} catch(err) {
					//skip it
				}
			}	
			try {
				var valSystemDisplName = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['value']['@codeSystemName'].toString();
			} catch(err) {
				var valSystemDisplName = '';
			}
			if (valSystemDisplName.length > 0) {
				try {
					msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['value']['@codeSystem'] = valSystemDisplName;
				} catch(err) {
					//skip it
				}
			}
		}
		// Remove this entry... there is no Question attached to it, and the answer is "unknown smoking status" even though subsequent smoking questions follow
		for (var j=0;j<numEntries;j++) {
			try {
				var textVal = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['id']['@extension'].toString();
			} catch(err) {
				var textVal = '';
			}
			if (textVal == '123456789') {
				delete msg['component']['structuredBody']['component'][i]['section']['entry'][j];
				break;
			}
		}		
		// Remove when there is just one entry that is set to "UNK"
		try {
			var numEntries = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
		} catch(err) {
			var numEntries = 0;
		}
		if (numEntries == 1) {
			try {
				var codeVal = ''+msg['component']['structuredBody']['component'][i]['section']['entry']['observation']['code']['@code'].toString();
			} catch(err) {
				var codeVal = '';
			}
			if (codeVal == 'UNK') {
				delete msg['component']['structuredBody']['component'][i]['section']['entry'][j];
				break;
			}				
		}
		break;
	}
}
}

/**
	CJ 1/21/2016 This function will update issues with the Social History section of CCD

	@param {String} arg1 - the CDA to be modified
	@return {String} return returns a modified CDA
*/
function updateSocialHistory_NG(arg1) {
// SOCIAL HISTORY **************************
default xml namespace = msg.namespace('');
try {
	numComps = msg['component']['structuredBody']['component'].length();
} catch(err) {
	var numComps = 0;
}
for (var i=0;i<numComps;i++) {
	try {
		var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
	} catch(err) {
		var compTitle = '';
	}
	if (compTitle.length == 0) {
		try {
			var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['title'].toString();
		} catch(err) {
			var compTitle = '';
		}
	}
 	if ((compTitle == '29762-2') || compTitle.toUpperCase().search("SOCIAL HISTORY") != -1) {
		// HTML: Remove the row if there is nothing in the Type column OR something in the Type column but nothing in either the Description or Quantity columns
		var done = false;
		var paranoiaCount = 0;
		while (!done) {
			paranoiaCount ++;
			try {
				var numRows = msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'].length();
			} catch(err) {
				var numRows = 0;
			}
			var foundEmptyLine = false;
			for (var j=0;j<numRows;j++) {
				try {
					var textVal1 = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][0]['content'].toString();
				} catch(err) {
					var textVal1 = '';
				}
				if (textVal1.length == 0) {
					delete msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j];
					foundEmptyLine = true;
					break;							
				} else {
					try {
						var textVal2 = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][1]['content'].toString();
						var hasContent = true;
					} catch(err) {
						var textVal2 = '';
						var hasContent = false;
					}						

					if (!hasContent) {
						try {
							var textVal2 = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][1].toString();
						} catch(err) {
							var textVal2 = '';
						}						
					}
					try {
						var textVal3 = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][2]['content'].toString();
						var hasContent = true;
					} catch(err) {
						var textVal3 = '';
						var hasContent = false;
					}						

					if (!hasContent) {
						try {
							var textVal3 = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][2].toString();
						} catch(err) {
							var textVal3 = '';
						}						
					}
					if ((textVal2.length == 0) && (textVal3.length == 0)) {
						delete msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j];
						foundEmptyLine = true;
						break;							
					}
				}
			}
			if (foundEmptyLine) {
				done = false;
			} else if (j >= numRows) {
				done = true;
			}
			if (paranoiaCount > 100) {
				done = true;
			}
		}
		// HTML: Make a list of the reference variables for later substitution in the XML.
 		try {
 			var numtrows = msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'].length();
 		} catch(err) {
 			var numtrows = 0;
 		}
 		var trowArr_name = new Array();
 		var trowArr_ref = new Array();
 		var trowArr_ans = new Array();
 		for (var j=0;j<numtrows;j++) {
 			try {
 				trowArr_name[j] = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][0]['content'].toString();
 			} catch(err) {
 				trowArr_name[j] = '';
 			}
 			try {
 				trowArr_ref[j] = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][0]['content']['@ID'].toString();
 			} catch(err) {
 				trowArr_ref[j] = '';
 			}
 			try {
 				trowArr_ans[j] = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][1]['content'].toString();
 			} catch(err) {
 				trowArr_ans[j] = '';
 			} 			
 			// If answer is blank, display text: "See Documents tab for the answer/details."
 			if (trowArr_ans[j].length == 0) {
 				trowArr_ans[j] = "See Documents tab for the answer/details.";
 			} 			
 		}


		// XML: Remove the entry if it was removed in the HTML
		try {
 			var numentries = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
 		} catch(err) {
 			var numentries = 0;
 		}
		for (k=numentries-1;k>=0;k--) {
 			try {
 				var entryRef = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['text']['reference']['@value'].toString();
 			} catch(err) {
 				var entryRef = '';
 			}
 			if (entryRef.length == 0) {
				try {
 					var entryRef = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['code']['originalText']['reference']['@value'].toString();
 				} catch(err) {
 					var entryRef = '';
 				} 						
 			}
 			if (entryRef.length > 2) {
 				var foundOrphanObs = true;
 				entryRef = entryRef.substr(1);				
				for (var j=0;j<numtrows;j++) {
 					var trowref = ''+trowArr_ref[j];							
 					if (trowref.toUpperCase() == entryRef.toUpperCase()) {
 						foundOrphanObs = false;
 						break;
 					}
				}
 			} else {
 				foundOrphanObs = false;
 			}
 			if (foundOrphanObs) {
				delete msg['component']['structuredBody']['component'][i]['section']['entry'][k];
			}
 		} 		  		

 		try {
 			var numentries = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
 		} catch(err) {
 			var numentries = 0;
 		}
 		for (var k=0;k<numentries;k++) {
 			// Do the NG fix
			// Smoking Status fix where observation id does not exist in entry
			var lenObs = msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation'].length();
			// // make sure there is at least 1 observation  in the CCD before trying to fix the missing ID for social history
			if (lenObs > 0)
			{
        			fnFixMissingSocialHistoryID(msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation'], lenObs);
			} // end if lenObs > 0 			
 			// XML: Overwrite "Question" and "Answer" with the reference variables from the HTML section.
 			// Substitute question text only if there is no code being sent
 			try {
 				var isQuestionCode = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['code']['@code'].toString();
  			} catch(err) {
 				var isQuestionCode = '';
 			} 	
 			// Substitute answer text only if there is no code being sent
 			try {
 				var isAnswerCode = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['value']['@code'].toString();
  			} catch(err) {
 				var isAnswerCode = '';
 			} 
 			try {
 				var entryRef = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['text']['reference']['@value'].toString();
 			} catch(err) {
 				var entryRef = '';
 			}
 			if (entryRef.length == 0) {
				try {
 					var entryRef = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['code']['originalText']['reference']['@value'].toString();
 				} catch(err) {
 					var entryRef = '';
 				} 						
 			} 				
 			if (entryRef.length > 2) {
 				entryRef = entryRef.substr(1);
 			}	
 			for (var j=0;j<numtrows;j++) {
 				var trowname = ''+trowArr_name[j];
 				var trowref = ''+trowArr_ref[j];
 				var trowans = ''+trowArr_ans[j];
 				if (trowref.toUpperCase() == entryRef.toUpperCase()) {
 					//Overwrite the question only if no code was sent
 					if (isQuestionCode.length == 0 || isQuestionCode.toUpperCase() == 'ASSERTION') {
 						try {
 							msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['code']['@code'] = trowname;
  						} catch(err) {
 							// skip it
 						}
 						try {
  							msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['code']['@displayName'] = trowname;
 						} catch(err) {
 							// skip it
 						} 	
 						try {
  							msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['code']['@codeSystem'] = '';
 						} catch(err) {
 							// skip it
 						} 
						try {
  							msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['code']['@codeSystemName'] = '';
 						} catch(err) {
 							// skip it
 						}  	
 					}
 					if (isAnswerCode.length == 0) {
 						try {
 							var ansType = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['value']['@xsi:type'].toString();
 						} catch(err) {
 							var ansType = '';
 						}
  						try {
 							var ansNull = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['value']['@nullFlavor'].toString()
 						} catch(err) {
 							var ansNull = '';
 						}
 						if (ansType != "CD") {
 							try {
 								msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['value'] = trowans;
 								// Above overwrites attributes, but need to add back in
 								msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['value']['@nullFlavor'] = ansNull;
 								msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['value']['@xsi:type'] = 'ST';
 							} catch(err) {
 								// skip it
 							}  	
 						}						
 					}
 					break;
 				}
 			} 					
 		}


 		// XML: MR requires a non-NULL ID in order to display the item.
		var done = false;
		var paranoiaCount = 0;
		while (!done) {
			paranoiaCount ++; 		
			try {
				var sectEntryCount = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
			} catch(err) {
				var sectEntryCount = 0;
			}
			for (var j=0;j<sectEntryCount;j++) { 			
				var needID = false;
				try {
					var isNull = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['id']['@nullFlavor'].toString();
					if (isNull == 'UNK') {
						needID = true;
					}
				} catch(err) {
					var isNull = '';
				}
				if (isNull.length == 0) {
					try {
						var isRoot = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['id']['@root'].toString();
					} catch(err) {
						var isRoot = '';
					}	
					if (isRoot.length == 0) {
						needID = true;
					}
				}
				done = true;
				if (needID) {
					done = false;
 					try {
 						var entryRef = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['text']['reference']['@value'].toString();
 					} catch(err) {
 						var entryRef = '';
 					}
 					if (entryRef.length == 0) {
						try {
 							var entryRef = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['code']['originalText']['reference']['@value'].toString();
 						} catch(err) {
 							var entryRef = '';
 						} 						
 					} 				
 					if (entryRef.length > 2) {
 						entryRef = entryRef.substr(1);
 					}		
 					if (entryRef.length > 2) {
 						var underscoreIdx = entryRef.search('_');
 						if (underscoreIdx != -1) {
 							entryRef = entryRef.substring(0,underscoreIdx);
 						}
 					}
 					try {
 						var idSegExists = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['id'].length();
 					} catch(err) {
 						var idSegExists = 0;
 					}
 					if (idSegExists > 0) {
						msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['id']['@nullFlavor'] = entryRef;
 					} else {
 						// insertBefore doesn't work, and appendChild adds it to the bottom. MR doesn't seem to like it there so this inserts the
 						// id element where the others are -- after the templateID element and before the code element
 						// Some do not send templateID element
						var obsStr = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation'].toString();
						var idSegVal = '<id root="'+entryRef+'"/><code';
						var obsStr = obsStr.replace("<code",idSegVal);
						var obsXml = new XML(obsStr);
						msg['component']['structuredBody']['component'][i]['section']['entry'][j] = obsXml;
						//update msg
						var msgStr = msg.toString();
						msg = new XML(msgStr);	
 					} // } else {
 					break; //Whenever the tree structure is changed, need to start all over otherwise Mirth fails
				} // if (needID) {
			} // for (var j=0;j<sectEntryCount;j++) { 
			if (paranoiaCount > 100) {done = true;}
 		}  // while (!done) {
 		break;
 	}  // if ((compTitle == '29762-2') ... {
} // for (var i=0;i<numComps;i++) {
} // function updateSocialHistory_NG(arg1) {

/**
	Cody's Edits

	@param {String} arg1 - the CDA to be modified
	@return {String} return returns a modified CDA
*/
function updateSocialHistory_NG_New_CJ(arg1) {
// SOCIAL HISTORY
default xml namespace = msg.namespace('');
logger.info("SH testvar: " + msg['component']['structuredBody']['component'].toString());
//logger.info('This is SocialHistory message: ' + msg)
//var test2 = msg.attributes();

try {numComps = msg['component']['structuredBody']['component'].length();
//logger.info('This is SocialHistory message Namespaces: ' + test2)
//msg['component']['structuredBody']['component'].length();
} catch(err) {
	var numComps = 0;
}
//logger.info('This is SocialHistory message after try: ' + msg)
for (var i=0;i<numComps;i++) {
	try {
		var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
	} catch(err) {
		var compTitle = '';
	}
	if (compTitle.length == 0) {
		try {
			var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['title'].toString();
		} catch(err) {
			var compTitle = '';
		}
	}
 	if ((compTitle == '29762-2') || compTitle.toUpperCase().search("SOCIAL HISTORY") != -1) {
		// HTML: Remove the row if there is nothing in the Type column OR something in the Type column but nothing in either the Description or Quantity columns
		var done = false;
		var paranoiaCount = 0;
		while (!done) {
			paranoiaCount ++;
			try {
				var numRows = msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'].length();
			} catch(err) {
				var numRows = 0;
			}
			var foundEmptyLine = false;
			for (var j=0;j<numRows;j++) {
				try {
					var textVal1 = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][0]['content'].toString();
				} catch(err) {
					var textVal1 = '';
				}
				if (textVal1.length == 0) {
					delete msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j];
					foundEmptyLine = true;
					break;							
				} else {
					try {
						var textVal2 = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][1].toString();
					} catch(err) {
						var textVal2 = '';
					}		
					try {
						var textVal3 = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][2].toString();
					} catch(err) {
						var textVal3 = '';
					}	
					if ((textVal2.length == 0) && (textVal3.length == 0)) {
						delete msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j];
						foundEmptyLine = true;
						break;							
					}
				}
			}
			if (foundEmptyLine) {
				done = false;
			} else if (j >= numRows) {
				done = true;
			}
			if (paranoiaCount > 100) {
				done = true;
			}
		}
		// HTML: Make a list of the reference variables for later substitution in the XML.
 		try {
 			var numtrows = msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'].length();
 		} catch(err) {
 			var numtrows = 0;
 		}
 		var trowArr_name = new Array();
 		var trowArr_ref = new Array();
 		var trowArr_ans = new Array();
 		for (var j=0;j<numtrows;j++) {
 			try {
 				trowArr_name[j] = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][0]['content'].toString();
 			} catch(err) {
 				trowArr_name[j] = '';
 			}
 			try {
 				trowArr_ref[j] = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][0]['content']['@ID'].toString();
 			} catch(err) {
 				trowArr_ref[j] = '';
 			}
 			try {
 				trowArr_ans[j] = ''+msg['component']['structuredBody']['component'][i]['section']['text']['table']['tbody']['tr'][j]['td'][1]['content'].toString();
 			} catch(err) {
 				trowArr_ans[j] = '';
 			} 			
 			// If answer is blank, display text: "See Documents tab for the answer/details."
 			if (trowArr_ans[j].length == 0) {
 				trowArr_ans[j] = "See Documents tab for the answer/details.";
 			} 			
 		}
 		try {
 			var numentries = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
 		} catch(err) {
 			var numentries = 0;
 		}
 		for (var k=0;k<numentries;k++) {
			// XML: Remove the entry if it was removed in the HTML
			var done = false;
			var paranoiaCount = 0;
			while (!done) {
				paranoiaCount ++;
 				try {
 					var entryRef = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['text']['reference']['@value'].toString();
 				} catch(err) {
 					var entryRef = '';
 				}
 				if (entryRef.length == 0) {
					try {
 						var entryRef = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['code']['originalText']['reference']['@value'].toString();
 					} catch(err) {
 						var entryRef = '';
 					} 						
 				}
 				if (entryRef.length > 2) {
 					var foundOrphanObs = true;
 					entryRef = entryRef.substr(1);				
					for (var j=0;j<numtrows;j++) {
 						var trowref = ''+trowArr_ref[j];							
 						if (trowref.toUpperCase() == entryRef.toUpperCase()) {
 							foundOrphanObs = false;
 							break;
 						}
					}
					if (foundOrphanObs) {
						delete msg['component']['structuredBody']['component'][i]['section']['entry'][k];
						break;		
					}
 				} else {
 					foundOrphanObs = false;
 				}
 				if (foundOrphanObs) {
					done = false;
				} else {
					done = true;
				}
				if (paranoiaCount > 100) {
					done = true;
				}
 			} 		  			
 			// XML: Overwrite "Question" and "Answer" with the reference variables from the HTML section.
 			// Substitute question text only if there is no code being sent
 			try {
 				var isQuestionCode = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['code']['@code'].toString();
  			} catch(err) {
 				var isQuestionCode = '';
 			} 	
 			// Substitute answer text only if there is no code being sent
 			try {
 				var isAnswerCode = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['value']['@code'].toString();
  			} catch(err) {
 				var isAnswerCode = '';
 			} 
 			try {
 				var entryRef = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['text']['reference']['@value'].toString();
 			} catch(err) {
 				var entryRef = '';
 			}
 			if (entryRef.length == 0) {
				try {
 					var entryRef = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['code']['originalText']['reference']['@value'].toString();
 				} catch(err) {
 					var entryRef = '';
 				} 						
 			} 				
 			if (entryRef.length > 2) {
 				entryRef = entryRef.substr(1);
 			}	
 			for (var j=0;j<numtrows;j++) {
 				var trowname = ''+trowArr_name[j];
 				var trowref = ''+trowArr_ref[j];
 				var trowans = ''+trowArr_ans[j];
 				if (trowref.toUpperCase() == entryRef.toUpperCase()) {
 					//Overwrite the question only if no code was sent
 					if (isQuestionCode.length == 0 || isQuestionCode.toUpperCase() == 'ASSERTION') {
 						try {
 							msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['code']['@code'] = trowname;
  						} catch(err) {
 							// skip it
 						}
 						try {
  							msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['code']['@displayName'] = trowname;
 						} catch(err) {
 							// skip it
 						} 	
 						try {
  							msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['code']['@codeSystem'] = '';
 						} catch(err) {
 							// skip it
 						} 
						try {
  							msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['code']['@codeSystemName'] = '';
 						} catch(err) {
 							// skip it
 						}  	
 					}
 					if (isAnswerCode.length == 0) {
 						try {
 							var ansType = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['value']['@xsi:type'].toString();
 						} catch(err) {
 							var ansType = '';
 						}
  						try {
 							var ansNull = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['value']['@nullFlavor'].toString()
 						} catch(err) {
 							var ansNull = '';
 						}
 						if (ansType != "CD") {
 							try {
 								msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['value'] = trowans;
 								// Above overwrites attributes, but need to add back in
 								msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['value']['@nullFlavor'] = ansNull;
 								msg['component']['structuredBody']['component'][i]['section']['entry'][k]['observation']['value']['@xsi:type'] = 'ST';
 							} catch(err) {
 								// skip it
 							}  	
 						}						
 					}
 					break;
 				}
 			} 					
 		}
 		// XML: MR requires a non-NULL ID in order to display the item.
		try {
			var sectEntryCount = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
		} catch(err) {
			var sectEntryCount = 0;
		}
		for (var j=0;j<sectEntryCount;j++) { 			
			var needID = false;
			try {
				var isNull = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['id']['@nullFlavor'].toString();
				if (isNull == 'UNK') {
					needID = true;
				}
			} catch(err) {
				var isNull = '';
			}
			if (isNull.length == 0) {
				try {
					var isRoot = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['id']['@root'].toString();
				} catch(err) {
					var isRoot = '';
				}	
				if (isRoot.length == 0) {
					needID = true;
				}
			}
			if (needID) {
 				try {
 					var entryRef = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['text']['reference']['@value'].toString();
 				} catch(err) {
 					var entryRef = '';
 				}
 				if (entryRef.length == 0) {
					try {
 						var entryRef = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['code']['originalText']['reference']['@value'].toString();
 					} catch(err) {
 						var entryRef = '';
 					} 						
 				} 				
 				if (entryRef.length > 2) {
 					entryRef = entryRef.substr(1);
 				}		
 				if (entryRef.length > 2) {
 					var underscoreIdx = entryRef.search('_');
 					if (underscoreIdx != -1) {
 						entryRef = entryRef.substring(0,underscoreIdx);
 					}
 				}
 				try {
 					var idSegExists = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['id'].length();
 				} catch(err) {
 					var idSegExists = 0;
 				}
 				if (idSegExists > 0) {
					msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['id']['@nullFlavor'] = entryRef;
 				} else {
 					// insertBefore doesn't work, and appendChild adds it to the bottom. MR doesn't seem to like it there so this inserts the
 					// id element where the others are -- after the templateID element and before the code element
 					// Some do not send templateID element
 					try {
 						var isTemplateID = msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['templateId'].length();
 					} catch(err) {
 						var isTemplateID = 0;
 					}
 					if (isTemplateID > 0) {
 						try {
 							var templateVal = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['templateId']['@root'].toString();
 						} catch(err) {
 							var templateVal = '';
 						}
 						if (templateVal.length > 0) {
 							templateVal += "AXXNSHID";
 						}
 						msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['templateId']['@root'] = templateVal;
 						var tmpMsgStr = ''+msg.toString();					
 						var splitMsg = tmpMsgStr.split(/AXXNSHID\"\/\>\<code/); 	
 						if (splitMsg.length == 2) {			
 							var newMsgStr = splitMsg[0]+'"/><id root="'+entryRef+'"/><code'+splitMsg[1];
 							msg = new XML(newMsgStr);
 						}  						
 					} else {
						try {
 							var ObsVal = ''+msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['@moodCode'].toString();
 						} catch(err) {
 							var ObsVal = '';
 						}
 						if (ObsVal.length > 0) {
 							ObsVal += "AXXNSHID";
 						}
 						msg['component']['structuredBody']['component'][i]['section']['entry'][j]['observation']['@moodCode'] = ObsVal; 
 						var tmpMsgStr = ''+msg.toString();					
 						var splitMsg = tmpMsgStr.split(/AXXNSHID\"\>\<code/); 	
 						if (splitMsg.length == 2) {			
 							var newMsgStr = splitMsg[0]+'"><id root="'+entryRef+'"/><code'+splitMsg[1];
 							msg = new XML(newMsgStr);
 						} 						
 					}
 				}
			}
		}
		break;
 	}
}
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function updateVitals_MP(arg1) {
default xml namespace = msg.namespace('');
// VITAL SIGNS
try {
	var numComps = msg['component']['structuredBody']['component'].length();
} catch(err) {
	var numComps = 0;
}
for (var i=0;i<numComps;i++) {
	try {
		var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
	} catch(err) {
		var compTitle = '';
	}
	if (compTitle.length == 0) {
		try {
			var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['title'].toString();
		} catch(err) {
			var compTitle = '';
		}
	}
	if ((compTitle == '8716-3') || compTitle.toUpperCase().search('VITAL SIGN') != -1) {
		// Remove component if only one entry and it's blank
		try {
			var numEntries = msg['component']['structuredBody']['component'][i]['section']['entry'].length();
		} catch(err) {
			var numEntries = 0;
		}
		if (numEntries == 1) {
			try {
				var codeVal = ''+msg['component']['structuredBody']['component'][i]['section']['entry']['organizer']['component']['observation']['code']['@code'].toString();
			} catch(err) {
				var codeVal = '';
			}
			try {
				var textVal = ''+msg['component']['structuredBody']['component'][i]['section']['entry']['organizer']['component']['observation']['value']['@value'].toString();
			} catch(err) {
				var textVal = '';
			}	
			if (codeVal == 'NI' && textVal == '0') {
				delete msg['component']['structuredBody']['component'][i];
				break;
			}
		}
		break;
	}
}
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function updateVitals_NG(arg1) {
// VITAL SIGNS ******************************
default xml namespace = msg.namespace('');
try {
	var numComps = msg['component']['structuredBody']['component'].length();
} catch(err) {
	var numComps = 0;
}
for (var i=0;i<numComps;i++) {
	try {
		var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['code']['@code'].toString();
	} catch(err) {
		var compTitle = '';
	}
	if (compTitle.length == 0) {
		try {
			var compTitle = ''+msg['component']['structuredBody']['component'][i]['section']['title'].toString();
		} catch(err) {
			var compTitle = '';
		}
	}
	if ((compTitle == "8716-3") || compTitle.toUpperCase().search("VITAL SIGNS") != -1) {
		// XML: MR requires a non-NULL ID in order to display the item. 
		try {
			var sectEntryCount = msg['component']['structuredBody']['component'][i]['section']['entry']['organizer']['component'].length();
		} catch(err) {
			var sectEntryCount = 0;
		}
		for (var j=0;j<sectEntryCount;j++) { 			
			try {
				var isNull = ''+msg['component']['structuredBody']['component'][i]['section']['entry']['organizer']['component'][j]['observation']['id']['@nullFlavor'].toString();
				if (isNull == 'UNK') {
					// Is there really something to show?
					var vitalCode = ''+msg['component']['structuredBody']['component'][i]['section']['entry']['organizer']['component'][j]['observation']['code']['@code'].toString();
					if (vitalCode.length > 0) {
						msg['component']['structuredBody']['component'][i]['section']['entry']['organizer']['component'][j]['observation']['id']['@nullFlavor'] = 'SUBID'+j.toString();
					}
				}
			} catch(err) {
				var isNull = '';
			}
			try {
				var unit = ''+msg['component']['structuredBody']['component'][i]['section']['entry']['organizer']['component'][j]['observation']['value']['@unit'].toString();
				unit = unit.toUpperCase();
			} catch(err) {
				var unit = '';
			}
			// Put in more descriptive text
			if (unit == '[IN_US]') {
				msg['component']['structuredBody']['component'][i]['section']['entry']['organizer']['component'][j]['observation']['value']['@unit'] = 'in';
			} else if (unit == '[LB_AV]') {
				msg['component']['structuredBody']['component'][i]['section']['entry']['organizer']['component'][j]['observation']['value']['@unit'] = 'lb';
			}
		}
		break;
	}
}
}
